import React, { Component } from "react";
import CardDisplay from "./components/card-display";
import info from "./card-info.json";
import Header from "./components/Header";
import Footer from "./components/Footer";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      score: 0,
      info,
      highScore: 0,
    };
  }
  clickHandle = (id) => {
    console.log("clicked", id);
    let newInfo = this.state.info.map((info) => {
      if (info.id === id) {
        if (info.clicked === false) {
          this.updateScore();
          if (this.state.score >= this.state.highScore) {
            this.updateHighScore();
            if (this.state.highScore === this.state.info.length) {
              this.winGame();
            }
          }
          info.clicked = true;
        } else {
          this.restartGame();
        }
      }
      return info;
    });
    this.shuffleCards(newInfo);
    console.log(newInfo);
    this.setState({ info: newInfo });
  };

  shuffleCards = (newInfo) => {
    newInfo.sort(() => Math.random() - 0.5);
  };

  updateScore = () => {
    this.setState({ score: this.state.score + 1 });
  };

  updateHighScore = () => {
    this.setState({ highScore: this.state.highScore + 1 });
  };

  winGame = () => {
    alert("You've won");
    this.setState({ highScore: 0 });
    this.restartGame();
  };
  restartGame = () => {
    this.setState({ score: 0 });
    let resetInfo = this.state.info.map((info) => {
      info.clicked = false;
      return info;
    });
    this.setState({ info: resetInfo });
  };
  render() {
    return (
      <div>
        <Header score={this.state.score} highScore={this.state.highScore} />

        <CardDisplay info={this.state.info} onClick={this.clickHandle} />
        <Footer />
      </div>
    );
  }
}

export default App;
